mapper.insert:
/*채팅방 생성*/
	INSERT INTO tb_c_chat_room(
		chat_room_code
		, user_id_code
		, chat_room_title
		, chat_room_content
		, chat_room_participant
		, chat_room_limit
		, chat_room_reg_date
		, chat_room_update
		, chat_room_nickname
	)VALUES (
		(SELECT 
			CONCAT('chat',LPAD(MAX(CAST(SUBSTRING(c.chat_room_code ,5)AS UNSIGNED)+1),2,0))
		FROM tb_c_chat_room AS c)
		, #{userIdCode}
		, #{chatRoomTitle}
		, #{chatRoomContent}
		, 0
		, #{chatRoomLimit}
		, NOW()
		, null
		, #{chatRoomNickName}
	); 
	
		SELECT
			c.chat_room_code
		FROM
			tb_c_chat_room AS c
		ORDER BY c.chat_room_code DESC
		LIMIT 1
