mapper.update:
/* 관리자 페이지 포인트 기준 수정 */
UPDATE 
	tb_pt_point_buy_save_by_grade 
SET 
	point_save_by_grade_percent = #{pointSaveByGradePercent}
   ,point_save_by_grade_unit = #{pointSaveByGradeUnit}
WHERE 
	point_buy_save_standard_grade_code = #{pointBuySaveStandardGradeCode};

mapper.delete:
/* 관리자 페이지 포인트 기준 내역 삭제 */
DELETE
FROM 
	tb_pt_point_buy_save_by_grade
WHERE 
	point_buy_save_standard_grade_code = #{pointBuySaveStandardGradeCode};

mapper.insert:
/* 관리자 페이지 포인트 기준 등록 */
INSERT INTO tb_pt_point_buy_save_by_grade
	 (point_buy_save_standard_grade_code
	,grade_user_code
	,point_standard_details_code
	,point_save_by_grade_percent
	,point_save_by_grade_unit)
	VALUES(
	(SELECT
		CONCAT('pbssgc0',MAX(CAST(SUBSTRING(pp.point_buy_save_standard_grade_code,7)AS UNSIGNED)+1))
	FROM 
		tb_pt_point_buy_save_by_grade AS pp)
	,#{pointSaveByGrade.gradeUserCode}
	,#{pointSaveByGrade.pointStandardDetailsCode}
	,#{pointSaveByGrade.pointSaveByGradePercent}
	,#{pointSaveByGrade.pointSaveByGradeUnit}
	);

mapper.select:
/* 관리자 페이지 수단 별 검색 내역 조회 */ SELECT pd.point_standard_details_code AS pointStandardDetailsCode ,pa.point_details_code AS pointDetailsCode ,pa.point_history_reg_date AS pointHistoryRegDate ,pa.user_id_code AS userIdCode ,pd.point_standard_name AS pointStandardName ,pd.point_standard_details_name AS pointStandardDetailsName ,pa.point_amount AS pointAmount FROM tb_pt_point_standard_details AS pd INNER JOIN tb_pt_point_accumulation_details AS pa ON pd.point_standard_details_code = pa.point_standard_details_code
AND ${searchKey} LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY pd.point_standard_details_code;
