mapper.insert:
/* 지출 내역 등록 */
INSERT INTO tb_gr_group_monthly_spending
(
	 spending_group_code
	,account_subject_code
	,spending_year
	,spending_month
	,account_name
	,spending_date
	,spending_amount
	,spending_info
	,user_id_code
	,spending_reg_date
) VALUES (
	(
	SELECT 
		CONCAT(date_format(NOW(),'%Y%m%d%H%i%s'),'_',a.account_subject_code)
	FROM
		tb_a_account_profit_and_loss_management AS a
	WHERE
		a.account_name = #{accountName}
	)
	,(
	SELECT
		a.account_subject_code
	FROM
		tb_a_account_profit_and_loss_management AS a
	WHERE
		a.account_name = #{accountName}
	)
	,YEAR(#{spendingDate})
	,MONTH(#{spendingDate})
	,#{accountName}
	,#{spendingDate}
	,#{spendingAmount}
	,#{spendingInfo}
	,#{userIdCode}
	,CURDATE()
);

